import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { withRouter, Link } from 'react-router-dom';
import signup from '../styles/SignUpPage.module.css';


const SignUpPage = ({ history, type }) => {
  const [alert, setAlert] = useState(false);

  useEffect(() => {
    if (localStorage.getItem("token")) {
      history.push('/list', null);
    }
  }, [alert]);

  const handleSubmit = () => {
    fetch("http://localhost:3001/auth/login", {
      method: "POST",
      body: `{
        "email": "sarah01@mail.com",
        "password": "123abc"
      }`,
      headers: {
        "Content-Length": 57,
        "Content-Type": "application/json; charset=utf-8"
      },
      credentials: "same-origin"
    }).then(function (response) {
      response.json().then(json => {
        localStorage.setItem("token", json.auth_token);
        history.push('/list', null);
      });
    }).catch(function (error) {
      console.log(error.message());
      return false;
    })
  }

  const clickHandler = e => {
    e.preventDefault();

    handleSubmit();
  };

  return (
    <main className={signup.main}>
      <header>
        <h1>{`Sign ${type === 'IN' ? 'in' : 'up'}`}</h1>
        <small>
          {type === 'IN' ? 'Welcome back! Sign in and meet with your friends' : 'Hello there! Sign up and start meeting new friends'}
        </small>
      </header>
      <form className={signup.form}>
        <label htmlFor="email">
          <input className={signup.input} id="email" type="email" placeholder="your@mail.com" required />
        </label>
        <label htmlFor="password">
          <input className={signup.input} id="password" type="password" required />
        </label>
        {type === 'IN' ? null : (
          <label htmlFor="confirmPassword">
            <input className={signup.input} id="confirmPassword" type="password" required />
          </label>
        )}
        <button onClick={clickHandler} type="submit" className="btn btn--orange">
          {type === 'IN' ? 'Sign in' : 'Sign up'}
        </button>
      </form>
      <Link to="/" className={signup.back}>{'< Back'}</Link>
    </main>
  );
};

SignUpPage.propTypes = {
  type: PropTypes.string.isRequired,
  history: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default withRouter(SignUpPage);
