import React, { useState, useRef, useEffect } from 'react';
import PropTypes from 'prop-types';
import { withRouter, Link } from 'react-router-dom';
import Nav from './Nav';
import list from '../styles/List.module.css';

const Favorites = ({ items }) => {
  const parent = useRef(null);

  const [navOpen, setNavOpen] = useState(false);
  const [fullText, setFullText] = useState(false);

  return (
    <main className={list['wrapper--favorites']} ref={parent}>
      <Nav open={navOpen} setNavOpen={setNavOpen} parent={parent} searchOpen={false} local="Favorites" />
      <h1 className={list.title}>Favorite Meets</h1>
      {items.length && items.map(item => (
        <>
        <article className={list.favorite}>
          <h3><Link to={`/details/${item.id}`}>{item.title}</Link></h3>

          <p>{item.frequency}</p>

          <p>{item.duration}h</p>
        </article>
          </>
      ))
      }
    </main>
  )
}

function mapStateToProps({ state }) {
  return { state: { ...state } };
}

function mapActionsToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...houseActions }, dispatch),
  };
}

Favorites.propTypes = {
  history: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default withRouter(Favorites);
