import React, { useEffect, useRef } from 'react';
import nav from '../styles/Nav.module.css';

const Nav = ({ open, setNavOpen, parent, searchOpen, useState }) => {
  const searchRef = useRef(null);
  const titleRef = useRef(null);

  //const [navOpen, setNavOpen] = useState(false);

  useEffect(() => {
    open && parent.current.classList.add('off-to-the-side');
    !open && parent.current.classList.remove('off-to-the-side');

    if (searchOpen) {
      searchRef.current.classList.remove('hidden');
      titleRef.current.classList.add('hidden');
    } else {
      searchRef.current.classList.add('hidden');
      titleRef.current.classList.remove('hidden');
    }
  }, [open, searchOpen]);

  const clickHandler = () => {
    setNavOpen(!open);
  };

  const handleClick = () => {
    console.log('AHHH');
  }

  return (
    <>
      <nav className={nav.main}>
        <ul>
          <li>
            <i onClick={clickHandler} className="fas fa-bars"></i>
          </li>
          <li>
            <h1 ref={titleRef} className={nav.title}>Houses</h1>
          </li>
          <li>
            <input ref={searchRef} className="search-nav hidden" type="text" placeholder="Search..." />
            <i onClick={handleClick} className="fas fa-search"></i>
          </li>
        </ul>
      </nav>
    </>
  )
}

export default Nav;
