import React, { useRef, useEffect } from 'react';
import app from '../styles/LandingPage.module.css';
import CarouselCard from './CarouselCard';

const Carousel = ({ users, controllers }) => {
  console.log(window.innerWidth)
  const scroller = useRef(null);
  const counter = useRef(null);
  const width = controllers ? 450 : 400;

  let cardPerView = 0;
  let currentCard = 0;

  useEffect(() => {
    scroller.current.scrollTo(0, 0);
    cardPerView = Math.floor(scroller.current.clientWidth / (width)); 
    currentCard = Math.floor((cardPerView/2) + (scroller.current.scrollLeft/width));
    document.getElementById(`card-${currentCard}`).classList.add('carousel__item--active');
    controllers && document.getElementById(`controller-${currentCard}`).classList.add('carousel__controller--on');
  }, [scroller]);

  const clickHandler = (e) => {
    const x = parseInt(e.target.dataset.x);
    scroller.current.scrollTo(x === 0 ? x : x - width, 0);

    [... new Array(users)].forEach((_user, i) => {
      if (i === x/width) {
        document.getElementById(`card-${i}`).classList.add('carousel__item--active');
        controllers && document.getElementById(`controller-${i}`).classList.add('carousel__controller--on');
      } else {
        document.getElementById(`card-${i}`).classList.remove('carousel__item--active');
        controllers && document.getElementById(`controller-${i}`).classList.remove('carousel__controller--on');
      }
    });
  }

  const scrollHandler = () => {
    const scrollTo = Math.floor((cardPerView/2) + (scroller.current.scrollLeft/width));
    document.getElementById(`card-${scrollTo}`).classList.add('carousel__item--active');
    controllers && document.getElementById(`controller-${scrollTo}`).classList.add('carousel__controller--on');

    if (scrollTo > 0) {
      document.getElementById(`card-${scrollTo - 1}`).classList.remove('carousel__item--active');
      controllers && document.getElementById(`controller-${scrollTo - 1}`).classList.remove('carousel__controller--on');  
      counter.current.innerText = !controllers ? scrollTo + 1: null;
    }

    if (scrollTo < users - 1) {
      document.getElementById(`card-${scrollTo + 1}`).classList.remove('carousel__item--active');
      controllers && document.getElementById(`controller-${scrollTo + 1}`).classList.remove('carousel__controller--on');  
      counter.current.innerText = !controllers ? scrollTo + 1 : null;
    }
  }

  return (
    <div onScroll={scrollHandler} ref={scroller} className={app['content--carousel']}>
      { [...new Array(users)].map((_item, i) => <CarouselCard key={i} id={`card-${i}`} type={controllers ? 'testemunial' : 'product'} />) }
      
      {
        controllers 
        ? (
          <div className={app['carousel__controllers']}>
            {[...new Array(users)].map((_item, i) => <div key={i} data-x={i * width} id={`controller-${i}`} onClick={clickHandler} className={'carousel__controller'}></div>) }
          </div>
        )
        : (
          <div className={app.count}>
            <span ref={counter}>1</span>/{users}
          </div>
        )
      }
    </div>
  )
}

export default Carousel;
