/* eslint-disable react/no-unescaped-entities */
/* eslint-disable react/jsx-props-no-spreading */
import React from 'react';
import { Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as houseActions from './actions/houseActions';
import LandingPage from './components/LandingPage';
import SignUpPage from './components/SignUpPage';
import IndexPage from './components/IndexPage';
import DetailsPage from './components/DetailsPage';
import FavoritesPage from './components/FavoritesPage';
import MessagesPage from './components/MessagesPage';
import SearchPage from './components/SearchPage';
import Page from './components/Page';
import List from './components/List';
//import { effect } from './helpers/effects';

const effect = (state, actions, history) => {
  if (state.authenticated &&
    (!localStorage.getItem("token") || localStorage.getItem("token") === 'undefined')) {
    localStorage.setItem('token', state.authenticated);
    localStorage.setItem('id', state.currentUser.id);
  } else if (localStorage.getItem("token") && !state.authenticated) {
    const token = localStorage.getItem("token");
    const currentU = localStorage.getItem('id');
    actions.fetchSubscription(`https://aqueous-wildwood-18424.herokuapp.com/users/${currentU}`, {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    }, null, true);
  } else if (!state.authenticated) {
    history.push('/sign_in', state);
  }
}

const App = () => {
  return (
    <Switch>
      <Route exact path="/page" render={() => <Page effects={[effect, () => {console.log('Test2')}]} dependencies={[[], []]} condition={true}><List /></Page>} />
      <Route exact path="/search/:term" component={SearchPage} />
      <Route exact path="/favorites" component={FavoritesPage} />
      <Route exact path="/messages" component={MessagesPage} />
      <Route exact path="/details/:id" component={DetailsPage} />
      <Route exact path="/list/" component={IndexPage} />
      <Route exact path="/sign_up/" render={() => <SignUpPage {...{ type: 'UP' }} />} />
      <Route exact path="/sign_in/" render={() => <SignUpPage {...{ type: 'IN' }} />} />
      <Route path="/" component={LandingPage} />
    </Switch>
  )
};

export default App;
