import React, { useRef } from 'react';
import app from '../styles/LandingPage.module.css';
import CarouselCard from './CarouselCard';

const Carousel = ({ users }) => {
  const scroller = useRef(null);

  const clickHandler = (e) => {
    const x = parseInt(e.target.dataset.x);
    scroller.current.scrollTo(x === 0 ? x : x - 125, 0);

    [... new Array(users)].forEach((_user, i) => {
      if (i === x/450) {
        document.getElementById(`card-${i}`).classList.add('carousel__item--active');
        document.getElementById(`controller-${i}`).classList.add('carousel__controller--on');
      } else {
        document.getElementById(`card-${i}`).classList.remove('carousel__item--active');
        document.getElementById(`controller-${i}`).classList.remove('carousel__controller--on');
      }
    });
  }

  return (
    <div ref={scroller} className={app['content--carousel']}>
      { [...new Array(users)].map((_item, i) => <CarouselCard key={i} id={`card-${i}`} />) }
      <div className={app['carousel__controllers']}>
        {[...new Array(users)].map((_item, i) => <div data-x={i * 450} id={`controller-${i}`} onClick={clickHandler} className={i === 0 ? 'carousel__controller carousel__controller--on' : 'carousel__controller'}></div>) }
      </div>
    </div>
  )
}

export default Carousel;
