/* eslint-disable react/no-unescaped-entities */
import React, { useState, useRef, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import Nav from './Nav';
import list from '../styles/List.module.css';
import Stars from './Stars';

const Detail = ({ history }) => {
  const parent = useRef(null);

  const [navOpen, setNavOpen] = useState(false);
  const [fullText, setFullText] = useState(false);

  useEffect(() => {
    if (navOpen === true) {
      setFullText(false);
    }
  }, [navOpen]);

  const clickHandler = () => {
    setFullText(!fullText);
  };

  const keyDownHandler = () => {
    clickHandler();
  };

  const handleClick = () => {
    history.pushState('/messages/');
  };

  return (
    <div className={list['wrapper--details']} ref={parent}>
      <Nav open={navOpen} setNavOpen={setNavOpen} parent={parent} searchOpen={false} location="Detail" />
      <main className={list[!fullText ? 'main--details' : 'main--details--open']}>
        <div className={list.image}>
          <div className={list.left}>
            <div className={list['profile-picture']}>
              <img src="https://upload.wikimedia.org/wikipedia/commons/8/89/Portrait_Placeholder.png" alt="User" />
            </div>
            <div>
              <h5>Sarah Moor</h5>
              <Stars points={3} />
            </div>
          </div>
          <div className={list.right}>
            <h5>$ 2,500</h5>
            <small>per month</small>
          </div>
        </div>
        <div className={list.description}>
          <h4>About this listing</h4>
          <p className={list[!fullText ? 'summary--min' : 'summary']}>
            Bojack Horseman's Princess Carolyn is a deep dive into the contemporary Working Woman. After this, watch Wisecrack's video on how Bojack Horseman and The Good Place explain death: https://youtu.be/YK6Rg2NEnVM Check out more Wisecrack! https://wscrk.com/3athNYF
            Princess Carolyn (Amy Sedaris) from Netflix’s Bojack Horseman is a role model for Working Women everywhere. She’s constantly fixing problems for her clients and friends, giving pep talks, and dominating in her field. But why is it so hard for her to accept help from others? Is she happy with her life by the end of the series? Watch this video for our look at Princess Caroyln’s story. If you like this video, subscribe to our channel or support us on Patreon: https://www.patreon.com/thetake
          </p>
          <div
            role="button"
            onClick={clickHandler}
            className={list.down}
            tabIndex={-1}
            aria-label={!fullText ? 'Show full text' : 'Hide text'}
            onKeyDown={keyDownHandler}
          >
            {
              !fullText
                ? (
                  <svg className={list['down-arrow']} aria-hidden="true" focusable="true" data-prefix="fal" data-icon="chevron-down" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                    <path fill="currentColor" d="M443.5 162.6l-7.1-7.1c-4.7-4.7-12.3-4.7-17 0L224 351 28.5 155.5c-4.7-4.7-12.3-4.7-17 0l-7.1 7.1c-4.7 4.7-4.7 12.3 0 17l211 211.1c4.7 4.7 12.3 4.7 17 0l211-211.1c4.8-4.7 4.8-12.3.1-17z" className="" />
                  </svg>
                ) : (
                  <svg className={list['down-arrow']} aria-hidden="true" focusable="true" data-prefix="fal" data-icon="chevron-up" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                    <path fill="currentColor" d="M4.465 366.475l7.07 7.071c4.686 4.686 12.284 4.686 16.971 0L224 178.053l195.494 195.493c4.686 4.686 12.284 4.686 16.971 0l7.07-7.071c4.686-4.686 4.686-12.284 0-16.97l-211.05-211.051c-4.686-4.686-12.284-4.686-16.971 0L4.465 349.505c-4.687 4.686-4.687 12.284 0 16.97z" className="" />
                  </svg>
                )
            }

          </div>
          <button onClick={handleClick} className="btn btn--orange--full" type="button">
            Apply to rent
          </button>
        </div>
      </main>
    </div>
  );
};

export default withRouter(Detail);
