import React, { useEffect, useState, useRef } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as houseActions from '../actions/houseActions';
import Nav from './Nav';
import Carousel from './Carousel';
import list from '../styles/List.module.css';
import Loader from './Loader';

const List = ({ actions, state }) => {
  const parent = useRef(null);

  useEffect(() => {
    actions.fetchSubscription(0);
  }, []);

  const [navOpen, setNavOpen] = useState(false);

  const { subscription } = state;

  const clickHandler = () => {
    if (navOpen) {
      setNavOpen(false);
    }
  }

  return (
    <div onClick={clickHandler} ref={parent} className={list.wrapper}>
      <Nav open={navOpen} setNavOpen={setNavOpen} parent={parent} searchOpen={false} location="Houses"/>
      <main className={list.main}>
        {subscription
          ? <Carousel users={subscription.length} />
          : <Loader />
        }
      </main>
    </div>
  )
}

function mapStateToProps({ state }) {
  return { state: { ...state } };
}

function mapActionsToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...houseActions }, dispatch),
  };
}

export default connect(mapStateToProps, mapActionsToProps)(List);
