import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as houseActions from '../actions/houseActions';
import { withRouter } from 'react-router-dom';
import nav from '../styles/HiddenNav.module.css';

const HiddenNav = ({ history, actions, state }) => {
  useEffect(() => {

  }, [state.currentUser]);

  const handleSubmit = async () => {
    actions.fetchSubscription('http://localhost:3001/auth/login', {
      method: "POST",
      body: `{
        "email": "sarah01@mail.com",
        "password": "123abc"
      }`,
      headers: {
        "Content-Length": 57,
        "Content-Type": "application/json; charset=utf-8"
      },
      credentials: "same-origin"
    }, 'SIGNOUT');
  }

  const clickHandler = e => {
    e.preventDefault();

    localStorage.removeItem('token');
    handleSubmit();

    setTimeout(() => {
      history.push('/', state);
    }, 1000);
  }

  return (
    <aside id="me" className={nav.main}>
      <nav className={nav.side}>
        <div className={nav.profile}>
          <a className={nav['profile-picture']} href="/">
            {state.currentUser ? state.currentUser.name : 'nouuu'}
            <img src="https://upload.wikimedia.org/wikipedia/commons/8/89/Portrait_Placeholder.png" alt="Current user profile" />
          </a>
          <h3><a href="/">Sarah Moor</a></h3>
          <small className={nav.nick}><a href="/">@moor</a></small>
        </div>
        <ul>
          <li><a href="/">Dashboard</a></li>
          <li><a href="/">Notifications</a></li>
          <li><a href="/">Messages</a></li>
          <li><a href="/">Groups</a></li>
          <li><a href="/">Statistics</a></li>
        </ul>
        <div className={nav['nav-footer']}>
          <ul>
            <li><a href="/">Help</a></li>
            <li><a href="#" onClick={clickHandler}>Logout</a></li>
          </ul>
        </div>
      </nav>
    </aside>
  );
};

function mapStateToProps({ state }) {
  return { state: { ...state } };
}

function mapActionsToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...houseActions }, dispatch),
  };
}

export default connect(mapStateToProps, mapActionsToProps)(withRouter(HiddenNav));
