import React, { useEffect, useState, useRef } from 'react';
import app from '../styles/LandingPage.module.css';

const Stars = ({ points }) => {
  const stars = useRef(null);
  const [point, setPoint] = useState(points);

  useEffect(() => {
    [...stars.current.children].reverse().forEach((star, i) => {
      if (i < point) {
        star.classList.add('star--on');
        star.classList.remove('star--off');
      }
    });
  }, [point, points, stars]);

  const clickHandler = (e) => {
    const dataPoints = parseInt(e.target.dataset.points);
    if (dataPoints !== stars) {
      setPoint(dataPoints);
    }
  };

  return (
    <div key={point} ref={stars} className={app.stars}>
      <i onClick={clickHandler} className="fas fa-star star star--off" data-points={5}></i>
      <i onClick={clickHandler} className="fas fa-star star star--off" data-points={4}></i>
      <i onClick={clickHandler} className="fas fa-star star star--off" data-points={3}></i>
      <i onClick={clickHandler} className="fas fa-star star star--off" data-points={2}></i>
      <i onClick={clickHandler} className="fas fa-star star star--off" data-points={1}></i>
  </div>
  )
}

export default Stars;
