import React, { useEffect } from 'react';
import nav from '../styles/Nav.module.css';

const Nav = ({ open, setNavOpen, parent }) => {
  useEffect(() => {
    open && parent.current.classList.add('off-to-the-side');
    !open && parent.current.classList.remove('off-to-the-side');
  }, [open]);

  const clickHandler = () => {
    setNavOpen(!open);
  };

  const handleClick = () => {
    console.log('AHHH');
  }

  return (
    <>
      <nav className={nav.main}>
        <ul>
          <li>
            <i onClick={clickHandler} className="fas fa-bars"></i>
          </li>
          <li>
            <h1>Houses</h1>
          </li>
          <li>
            <i onClick={handleClick} className="fas fa-search"></i>
          </li>
        </ul>
      </nav>
    </>
  )
}

export default Nav;
