import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import Cover from './Cover';
import nav from '../styles/Nav.module.css';

const Nav = ({ open, setNavOpen, parent, searchOpen, location }) => {
  const searchRef = useRef(null);
  const titleRef = useRef(null);

  const [search, setSearch] = useState(searchOpen);

  useEffect(() => {
    open && parent.current.classList.add('off-to-the-side');
    !open && parent.current.classList.remove('off-to-the-side');

    if (search) {
      searchRef.current.classList.remove('hidden');
      titleRef.current.classList.add('hidden');
      searchRef.current.parentElement.classList.add('exposed');
      titleRef.current.parentElement.classList.add('collapsed');
    } else {
      searchRef.current.classList.add('hidden');
      titleRef.current.classList.remove('hidden');
      searchRef.current.parentElement.classList.remove('exposed');
      titleRef.current.parentElement.classList.remove('collapsed');
    }
  }, [open, searchOpen, search]);

  const clickHandler = () => {
    setNavOpen(!open);
  };

  const handleClick = (coverUp = false) => {
    if (coverUp === true) {
      setSearch(false);
    } else if (!search) {
      setSearch(!search);
    } else {
      console.log('Make search logic call here');
    }
  }

  return (
    <>
      <nav className={nav.main}>
        <ul>
          <li>
            <i onClick={clickHandler} className="fas fa-bars"></i>
          </li>
          <li>
            { location.toLowerCase() === 'detail'
              ? (
                  <svg className={nav.ico} aria-hidden="true" focusable="true" data-prefix="fal" data-icon="chevron-left" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 256 512">
                    <path fill="currentColor" d="M231.293 473.899l19.799-19.799c4.686-4.686 4.686-12.284 0-16.971L70.393 256 251.092 74.87c4.686-4.686 4.686-12.284 0-16.971L231.293 38.1c-4.686-4.686-12.284-4.686-16.971 0L4.908 247.515c-4.686 4.686-4.686 12.284 0 16.971L214.322 473.9c4.687 4.686 12.285 4.686 16.971-.001z" class=""></path>
                  </svg>
                )

              : null
            }
            <h1 key={search} ref={titleRef} className={nav.title}>{location}</h1>
          </li>
          <li>
            <input key={search} ref={searchRef} className="search-nav hidden" type="text" placeholder="Search..." />
            <i onClick={handleClick} className="fas fa-search search-ico"></i>
          </li>
        </ul>
      </nav>
      {search ? <Cover onClick={() => handleClick(true)} /> : null }
    </>
  )
}

export default Nav;
