import React from 'react';
import PropTypes from 'prop-types';

class NewMessagemForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: '',
      conversation_id: this.props.conversation_id,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  UNSAFE_componentWillReceiveProps(nextProps) {
    this.setState({ conversation_id: nextProps.conversation_id });
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    const token = localStorage.getItem('token');

    e.preventDefault();

    fetch('https://aqueous-wildwood-18424.herokuapp.com/messagems', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
        Accept: 'application/json',
      },
      body: JSON.stringify(this.state),
    });
    this.setState({ text: '' });
  }

  render() {
    const { state, handleChange, handleSubmit } = this;
    return (
      <div className="newMessagemForm">
        <form onSubmit={handleSubmit}>
          <label>New Messagem:
            <input
              id="message"
              type="text"
              value={state.text}
              onChange={handleChange}
            />
          </label>
          <input type="submit" />
        </form>
      </div>
    );
  }
}

NewMessagemForm.propTypes = {
  conversation_id: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default NewMessagemForm;
