import React, { useRef } from 'react';
import app from '../styles/LandingPage.module.css';
import CarouselCard from './CarouselCard';

const Carousel = ({ users }) => {
  const scroller = useRef(null);

  const clickHandler = (e) => {
    console.log(scroller.current.scrollTo(e.target.dataset.x, 0));
  }

  return (
    <div ref={scroller} className={app['content--carousel']}>
      { [...new Array(users)].map(_item => <CarouselCard />) }
      <div className={app['carousel__controllers']}>
        {[...new Array(users)].map((_item, i) => <div data-x={i * 10} onClick={clickHandler} className={app[i === Math.floor(users/2) ? 'carousel__controller--on' : 'carousel__controller']}></div>) }
      </div>
    </div>
  )
}

export default Carousel;
