import React, { useState, useRef, useEffect } from 'react';
import Nav from './Nav';
import list from '../styles/List.module.css';

const Favorites = ({ items }) => {
  const parent = useRef(null);

  const [navOpen, setNavOpen] = useState(false);
  const [fullText, setFullText] = useState(false);

  return (
    <main className={list['wrapper--favorites']} ref={parent}>
      <Nav open={navOpen} setNavOpen={setNavOpen} parent={parent} searchOpen={false} location="Detail" />
      <h1>Favorite Meets</h1>
      {items.length && items.map(item => (
        <article className={list.favorite}>
          <h3><Link to={`/details/${item.id}`}>{item.title}</Link></h3>
          <p>{item.day}</p>

          <p>{item.description}</p>

          <p>{item.duration}</p>

          <p>{item.frequency}</p>

          <p>{item.max_members}</p>

          <p>{item.members}</p>
        </article>
      ))
      }
    </main>
  )
}

export default Favorites;
