import React, { useEffect, useState, useRef } from 'react';
import app from '../styles/LandingPage.module.css';

const Stars = ({ points }) => {
  const stars = useRef(null);
  const [point, setPoint] = useState(points);

  useEffect(() => {
    [...stars.current.children].forEach(star => {
      console.log(star.classList)
    });
  }, [point]);

  return (
    <div ref={stars} className={app.stars}>
      <i className="fas fa-star star star--off"></i>
      <i className="fas fa-star star star--off"></i>
      <i className="fas fa-star star star--off"></i>
      <i className="fas fa-star star star--off"></i>
      <i className="fas fa-star star star--on"></i>
  </div>
  )
}

export default Stars;
