import React from 'react';
import PropTypes from 'prop-types';

class NewMessagemForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: '',
      conversation_id: this.props.conversation_id
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    this.setState({ conversation_id: nextProps.conversation_id });
  };

  handleChange(e) {
    this.setState({ text: e.target.value });
  };

  handleSubmit(e) {
    const token = localStorage.getItem('token');

    e.preventDefault();

    fetch(`https://aqueous-wildwood-18424.herokuapp.com/messagems`, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
        Accept: 'application/json',
      },
      body: JSON.stringify(this.state)
    });
    this.setState({ text: '' });
  };

  render() {
    return (
      <div className="newMessagemForm">
        <form onSubmit={this.handleSubmit}>
          <label>New Messagem:</label>
          <br />
          <input
            type="text"
            value={this.state.text}
            onChange={this.handleChange}
          />
          <input type="submit" />
        </form>
      </div>
    );
  };
}

NewMessagemForm.propTypes = {
  conversation_id: PropTypes.objectOf(PropTypes.any).isRequired,
};

export default NewMessagemForm;
