/* eslint-disable react/no-unescaped-entities */
/* eslint-disable react/jsx-props-no-spreading */
import React, { useState } from 'react';
import { Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import * as houseActions from './actions/houseActions';
import LandingPage from './components/LandingPage';
import SignUpPage from './components/SignUpPage';
import IndexPage from './components/IndexPage';
import DetailsPage from './components/DetailsPage';
import MessagesPage from './components/MessagesPage';

const App = ({ actions, state }) => {
  const handleAuth = async (history) => {
    let res;
    const token = localStorage.getItem('token');
    await fetch('http://localhost:3001/meets', {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    })
    .then(resp => {
      if (resp.status === 200) {
        resp.json();
      } else {
        throw 'Could not authenticate';
      }
    })
    .then(data => {
      res = true;
      return data;
    })
    .catch(_err => {
      history.push('/', null);
      return false;
    });

    await res;
    return (await res);
  };

  return (
    <Switch>
      <Route exact path="/messages" component={MessagesPage} />
      <Route exact path="/details/:id" component={DetailsPage} />
      <Route exact path="/list/" component={IndexPage} />
      <Route exact path="/sign_up/" render={() => <SignUpPage {...{ type: 'UP', handleAuth: handleAuth }} />} />
      <Route exact path="/sign_in/" render={() => <SignUpPage {...{ type: 'IN', handleAuth: handleAuth }} />} />
      <Route exact path="/" component={LandingPage} />
    </Switch>
  )
};

function mapStateToProps({ state }) {
  return { state: { ...state } };
}

function mapActionsToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...houseActions }, dispatch),
  };
}

export default connect(mapStateToProps, mapActionsToProps)(App);
