import React, { useRef } from 'react';
import app from '../styles/LandingPage.module.css';
import CarouselCard from './CarouselCard';

const Carousel = ({ users }) => {
  const scroller = useRef(null);

  const clickHandler = (e) => {
    const x = parseInt(e.target.dataset.x);
    console.log(scroller.current.scrollTo(x === 0 ? x : x - 125, 0));

    [... Array.new(users)].forEach((user, i) => {
      document.getElementById(`card-${i}`).classList.remove('carousel__item--active');
    });
    document.getElementById(`card-${(x/450)}`).classList.add('carousel__item--active');
  }

  return (
    <div ref={scroller} className={app['content--carousel']}>
      { [...new Array(users)].map((_item, i) => <CarouselCard key={i} id={`card-${i}`} />) }
      <div className={app['carousel__controllers']}>
        {[...new Array(users)].map((_item, i) => <div data-x={i * 450} onClick={clickHandler} className={app[i === Math.floor(users/2) ? 'carousel__controller--on' : 'carousel__controller']}></div>) }
      </div>
    </div>
  )
}

export default Carousel;
